import styled from 'styled-components'

export const PropertyHeaderWrapper = styled.header`
  display: flex;
  justify-content: space-between;
  padding: 5px 0;
  .container-icon{
  padding: 5px ;
     border:  1px solid #ccc;
     border-radius: 10px;
  }
`;

export const PropertyImageWrapper = styled.div`
border-radius: 20px;
margin: 10px 0;
img {
height: 100%;
width: 100%;
border-radius: inherit;
}
 
`;

export const PropertyInforWrapper = styled.section`
 h3 {
 font-size: 1.19em;
 }
.price {
font-weight: bold;
color: #5078E1;
font-size: 1.5em;
margin-top: 5px;
}

 .info-secudary {
   display: flex;
   align-items: center;
   .container-secundary {
   width: 100%;
   border: 1px solid red;
      display: flex;
      align-items: center;
      justify-content: space-between;
     .info-secudary-margin{ 
     display: flex;
     justify-content: space-between;
     margin-right: 5px;
      border: 1px solid red;
      flex: 8;
     }
   }
   }

.info-secudary-type {
   display: flex;

   align-items: center;
  .info-type-margin {
   margin: 0 10px 0 5px; 
  }
} ;
`
export const PropertyDescripcitionWrapper = styled.section`

h4 {
margin: 0;
color: #121F40;
text-transform: capitalize;
}
p {
color: #878D9E;
text-align: start;
}
`
