import styled from 'styled-components'

export const PropertyHeaderWrapper = styled.header`
  display: flex;
  justify-content: space-between;
  padding: 5px 0;
  .container-icon{
  padding: 5px ;
     border:  1px solid #ccc;
     border-radius: 10px;
  }
`;

export const PropertyImageWrapper = styled.div`
border-radius: 20px;
margin: 10px 0;
img {
height: 100%;
width: 100%;
border-radius: inherit;
}
 
`;

export const PropertyInforWrapper = styled.section`
 h3 {
 font-size: 1.19em;
 }
.price {
font-weight: bold;
color: #5078E1;
font-size: 1.5em;
margin-top: 5px;
}

 .info-secudary {
   display: flex;
   align-items: center;
   .container-secundary {
      width: 100%;
      display: flex;
      align-items: center;
      justify-content: space-between;
     .info-container-secudary-type{ 
     display: flex;
     justify-content: end;
     align-items: center;
      flex: 8;
      
      .info-secudary-margin {
         margin-right: 10px;
      }
     }
   }
   }

.info-secudary-type {
   display: flex;

   align-items: center;
  .info-type-margin {
   margin: 0 10px 0 5px; 
  }
} ;
`
export const PropertyDescripcitionWrapper = styled.section`

h4 {
margin: 0;
color: #121F40;
text-transform: capitalize;
}
p {
color: #878D9E;
text-align: start;
}
`
export const PropertysellerInfoWrapper = styled.section `
display: flex;
border: 1px solid red;
justify-content: space-between;
align-items: center;
.sale-info {
display: flex;
flex: 7;
border: 1px solid red;
.imagen {
flex: 3;
border-radius: 20px;
height: 50%;
img {
border-radius: inherit;
}
}

.seller-info {
flex: 7;
 p {
 margin: 0;
 }
}
}

.info-sale-icon {
    flex: 3;
    display: flex;
    justify-content: end;
    .icons {
     margin-right: 5px;
     padding:  5px;
     border:  1px solid #ccc;
     border-radius: 10px;
    max-height: 50%;
     svg {
     
     font-size: 2em;
     }
    }
}

`
